SET NAMES utf8mb4;

DROP TABLE IF EXISTS `email_content`;
CREATE TABLE `email_content` (
  `email_content_id` int NOT NULL AUTO_INCREMENT,
  `language_id` int NOT NULL DEFAULT '1',
  `email_name` varchar(64) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `email_description` varchar(255) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `from_name` varchar(64) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `from_email` varchar(128) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `subject` varchar(255) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `content_text` longtext COLLATE utf8mb4_general_ci NOT NULL,
  `content_html` longtext COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (`email_content_id`,`language_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

